INSERT INTO [dbo].[category] ([Id], [GuidValue], [CTime], [UTime], [IsDeleted], [Name], [Description], [Slug]) VALUES (1, N'60c28e74-698c-4824-9f7f-0c19fcef584f', N'2020-01-26 17:28:42', N'2020-01-26 17:51:08', 0, N'Shooter games', N'In shooter games (or simply shooters), players use ranged weapons to participate in the action, which takes place at a distance.[2] Most shooters involve violent gameplay; lethal weaponry is used to damage opponents. However, some shooters, such as Splatoon, have non-violent objectives.', N'shooter-games')
INSERT INTO [dbo].[category] ([Id], [GuidValue], [CTime], [UTime], [IsDeleted], [Name], [Description], [Slug]) VALUES (2, N'349f7d17-594e-4aca-b445-3ad99c879f4c', N'2020-01-26 17:29:19', N'2020-01-26 17:29:19', 0, N'Fighting games', N'Fighting games center around close-ranged combat typically one on one fights or against a small number of equally powerful opponents, often involving violent and exaggerated unarmed attacks.', N'fighting-games')
INSERT INTO [dbo].[category] ([Id], [GuidValue], [CTime], [UTime], [IsDeleted], [Name], [Description], [Slug]) VALUES (3, N'16569b4b-da18-4f70-8093-d0268598036d', N'2020-01-26 17:29:51', N'2020-01-26 17:29:51', 0, N'Stealth game', N'These games tend to emphasize sneaking around and avoiding enemy notice over direct conflict, for example, the Metal Gear series, and the Sly Cooper series. In a Stealth game, players are usually still able to engage in loud, conspicuous combat, but are often punished for it. ', N'stealth-game')
INSERT INTO [dbo].[category] ([Id], [GuidValue], [CTime], [UTime], [IsDeleted], [Name], [Description], [Slug]) VALUES (4, N'5457dc03-a92d-498a-bf44-08a38b1bc485', N'2020-01-26 17:30:12', N'2020-01-26 17:30:12', 0, N'Survival games', N'Survival games start the player off with minimal resources, in a hostile, open-world environment, and require them to collect resources, craft tools, weapons, and shelter, in order to survive as long as possible.', N'survival-games')
INSERT INTO [dbo].[category] ([Id], [GuidValue], [CTime], [UTime], [IsDeleted], [Name], [Description], [Slug]) VALUES (5, N'04d2d465-b2e9-4341-af8f-b7b6db092da5', N'2020-01-26 17:30:35', N'2020-01-26 17:30:35', 0, N'Adventure', N'Adventure games were some of the earliest games created, beginning with the text adventure Colossal Cave Adventure in the 1970s. That game was originally titled simply "Adventure," and is the namesake of the genre. Over time, graphics have been introduced to the genre and the interface has evolved.', N'adventure')
